    // MENU SETUP
    function menuSettings() {
        // detect drop down for adding class
        $(".main-menu > ul > li").find("> ul").parent().addClass("dropdown");

        // var stikyHead = $(".header").html();
        // $(".sticky-header").html(stikyHead);

        // create mobile menu
        var menuHtml = $(".main-menu").html();
        $(".mobile-menu").html(menuHtml);
        // var metaInfo = $(".meta-info").html();

        // mobile menu toggle

    }
    menuSettings();

    //Dropdown menu

    $(".main-menu > ul > li").hover(function () {
        $(this).find("> a").addClass("hoverClass");
        $(this).find(">ul").stop(true, false).slideDown(200);
    }, function () {
        $(this).find("> a").removeClass("hoverClass");
        $(this).find(">ul").stop(true, false).slideUp(200);
    });


    function menuMobile() {
        // drp menu 
        var allAccordion = $('.mobile-menu >ul > li > ul');
        var allAccordionItem = $('.mobile-menu >ul > li >a');
        $(allAccordionItem).click(function () {

            if ($(this).hasClass('open')) {
                $(this).removeClass('open');
                $(this).next().slideUp(200);
            }
            else {
                allAccordion.slideUp(200);
                allAccordionItem.removeClass('open');
                $(this).addClass('open');
                $(this).next().slideDown(200);
                return false;
            }
        });
    }
    menuMobile();


    $('a.menu-toggle').click(function () {
        $(this).toggleClass("active");
        $(this).parent().toggleClass("show-it");
    });
    $('body').click(function () {
        $('.mobile-menu').removeClass("show-it");
        $('a.menu-toggle').removeClass("active");
    });
    $('.mobile-menu,a.menu-toggle').click(function (event) {
        event.stopPropagation();
    });
